import React from 'react';
import Card from "../Card/Card";
import './MoviesCardList.css'

const MoviesCardList = ({ movieList, addSavedMovie, savedMovies, deleteSavedMovie }) => {

    const [deviceLayout, setDeviceLayout] = React.useState({
        desktop: { quantity: 12, forAdd: 4 },
        notebook: { quantity: 12, forAdd: 4 },

    });

    const [shownMovieList, setShownMovieList] = React.useState(movieList.slice(0, desktopLayout.quantity));

    console.log(window.innerWidth);

    // const tabletMovieList = movieList.slice(0, 7);
    // const mobileMovieList = movieList.slice(0, 4);

    const addMoreCards = (layoutStyle) => {
        const forAdd = desktopLayout.forAdd;
        const quantity = desktopLayout.quantity;

        if (shownMovieList.length + forAdd <= movieList.length) {
            const newQuantity = quantity + forAdd;
            setDesktopLayout({ ...desktopLayout, quantity: newQuantity });
            setShownMovieList(movieList.slice(0, newQuantity));
        }

        if (shownMovieList.length + forAdd > movieList.length) {
            setShownMovieList(movieList);
        }
    }

    const isSaved = ((movie) => {
        return savedMovies.find(saved => {
            return saved.movieId === movie.id
        })
    })


    return (
        <section className="cards">
            <div className="cards__inner">
                <ul className="card-list">
                    {
                        shownMovieList.map((movie) =>
                        <Card
                            isSaved={isSaved(movie)}
                            movie={movie}
                            key={movie.id}
                            saveMovie={addSavedMovie}
                            deleteMovie={deleteSavedMovie}
                        />)
                    }
                </ul>
                {
                    shownMovieList.length < movieList.length &&
                    <button className="card-list__more" onClick={addMoreCards}>Ещё</button>
                }
            </div>
        </section>
    );
};

export default MoviesCardList;
