import React from 'react';
import './Profile.css';
import { useFormWithValidation } from "../../hooks/useValidation";

const Profile = ({ signOut, auth }) => {

    const { values, handleChange, errors, isValid, resetForm, setValues } = useFormWithValidation();


    React.useEffect(() => {
        setValues(auth);
    }, [currentUser, setValues]);

    return (
        <section className="profile">
            <p className="profile__greeting">Привет, {auth.name}</p>
            <form className="profile__info">
                <div className="profile__block">
                    <p className="profile__text">Имя</p>
                    <input className="profile__input" name="name"/>
                    <p className="profile__text">E-mail</p>
                    <input className="profile__input" name="email"/>
                </div>
                <div className="profile__buttons">
                    <span className="profile__buttons-error">При обновлении профиля произошла ошибка.</span>
                    <button
                        type="submit"
                        disabled={!isValid}
                        className="profile__button profile__button_edit">
                        Редактировать
                    </button>
                    <button
                        onClick={signOut}
                        type="button"
                        className="profile__button profile__button_logout">
                        Выйти из аккаунта
                    </button>
                </div>
            </form>
        </section>
    );
};

export default Profile;
