import './App.css';
import Header from '../../components/Header/Header';
import Main from "../Main/Main";
import Movies from "../Movies/Movies";
import { Route, Switch, useLocation } from 'react-router-dom';
import Footer from "../Footer/Footer";
import React from "react";
import SavedMovies from "../SavedMovies/SavedMovies";
import deleteCard from "../../images/delete.svg";
import unbookmark from "../../images/save.svg";
import bookmark from "../../images/save-transparent.svg";

function App() {

    const [isSaved, setIsSaved] = React.useState(false);
    const location = useLocation();

    const handleSave = () => {
        setIsSaved(!isSaved);
    }

    const handleImage = () => {
        if (location.pathname === '/saved-movies') {
            return <img src={deleteCard} alt="Значок удаления закладки"/>
        } else if (isSaved) {
            return <img src={unbookmark} alt="Значок закладки активный"/>
        } else {
            return <img src={bookmark} alt="Значок закладки неактивный"/>
        }
    }

  return (
      <div className="page">
              <Header/>
          <Switch>
              <Route exact path="/">
                  <Main/>
              </Route>
              <Route path="/movies">
                  <Movies handleSave={handleSave}/>
              </Route>
              <Route path="/saved-movies">
                  <SavedMovies isSaved={isSaved} savedMovies={savedMovies}/>
              </Route>
          </Switch>
          <Footer/>
      </div>
  );
}

export default App;
