import React from 'react';
import logo from '../../images/logo.svg';
import './Header.css';
import { Route, Routes, useLocation } from "react-router-dom";
import NavMovieBlock from "../NavMovieBlock/NavMovieBlock";
import NavAuthBlock from "../NavAuthBlock/NavAuthBlock";

const Header = () => {

    const location = useLocation();

    return (
        <header className={
            location.pathname === '/'
                ? 'header header__about'
                : 'header header__main'
        }>
            <img className="header__logo" src={logo} alt="logo"/>
            <div className="header__routes">
                <Routes>
                    <Route path="/" element={<NavAuthBlock />}/>
                    <Route path={["/movies", "/saved-movies"]} element={<NavMovieBlock />}/>
                </Routes>
            </div>
        </header>
    );
};

export default Header;
