import React from 'react';
import SearchForm from "../SearchForm/SearchForm";
import MoviesCardList from "../MoviesCardList/MoviesCardList";
import moviesApi from "../../utils/MoviesApi";

const Movies = () => {

    const [movieList, setMovieList] = React.useState([]);
    const [showShorts, setShowShorts] = React.useState(true);
    const [search, setSearch] = React.useState(null);

    function fetchMovieList() {
        moviesApi.getBeatMovies()
            .then((data) => {
                setMovieList(data);
            })
            .catch((data) => {
                console.log(data);
            })
    }

    React.useEffect(() => {
        fetchMovieList();
    })

    const handleSearchText = (evt) => {
        setSearch(evt.target.value)
    }

    const handleShortsTumbler = () => {
        setShowShorts(!showShorts);
    }

    return (
        <main>
            <SearchForm
                showShorts={showShorts}
                handleShortsTumbler={handleShortsTumbler}
                search={search}
                handleSearchText={handleSearchText}
            />
            <MoviesCardList movieList={movieList}/>
        </main>
    );
};

export default Movies;
