class MainApi {
    constructor({ url, headers }) {
        this._url = url;
        this._headers = headers;
    }

    _isResOk(res) {
        return res.ok ? res.json() : Promise.reject(`Ошибка: ${res.status}`);
    }

    addMovie(data) {
        const {
            duration,
            nameRU,
            nameEN,
            id
        } = data;

        const imageUrl = (data) => {
            return `https://api.nomoreparties.co${data.image.url}`
        }

        return fetch(`${this._url}/movies/`, {
            method: "POST",
            headers: this._headers,
            body: JSON.stringify({
                movieId: id,
                duration,
                image: imageUrl(data),
                nameRU,
                nameEN,
            })
        }).then(this._isResOk);
    }
}

const mainApi = new MainApi({
    url: "https://api.movies.app.nomoredomains.xyz",
    headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
        authorization: "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MmIyMmI0NDQyYjNkYzRiZjAyZmUxNjgiLCJpYXQiOjE2NTczMTE0MDEsImV4cCI6MTY1NzkxNjIwMX0.hC1hAxxVLsZfwyI3E4CKagtNoF4Nkn1efFdxODvuntA",
    },
});

export default mainApi;
