import React from 'react';
import logo from "../../images/logo.svg";
import { Link } from "react-router-dom";
import './Register.css';

const Register = () => {
    return (
        <div className="sign">
            <img src={logo} alt="Логотип"/>
            <h2 className="sign__title">Добро пожаловать!</h2>
            <form className="form">
                <p className="form__description">Имя</p>
                <input className="form__input" type="text"/>
                <span className="form__error-text" id="name-error"/>
                <p className="form__description">E-mail</p>
                <input className="form__input" type="email"/>
                <span className="form__error-text" id="email-error"/>
                <p className="form__description">Пароль</p>
                <input className="form__input" type="password"/>
                <span className="form__error-text" id="password-error">Что-то пошло не так...</span>
                <button className="form__button">Зарегистрироваться</button>
            </form>
            <p className="sign__redirect-text">
                Уже зарегистрированы?
                <Link to="/signin" className="sign__redirect-link">Войти</Link>
            </p>
        </div>
    );
};

export default Register;
