import React from 'react';
import SearchForm from "../SearchForm/SearchForm";
import MoviesCardList from "../MoviesCardList/MoviesCardList";
import moviesApi from "../../utils/MoviesApi";

const Movies = () => {

    const [movieList, setMovieList] = React.useState([]);
    // const [search, setSearch] = React.useState(null);
    const [filter, setFilter] = React.useState({
        shorts: true,
        search: '',
    })

    function fetchMovieList() {
        moviesApi.getBeatMovies()
            .then((data) => {
                setMovieList(data);
            })
            .catch((data) => {
                console.log(data);
            })
    }

    React.useEffect(() => {
        fetchMovieList();
    })

    const handleShortsTumbler = () => {
        setFilter({
            ...filter,
            shorts: !filter.shorts,
        });
    }

    const filteredMovieList = () => {
        if (filter.shorts === false || filter.search === '') {
            return movieList;
        }

        setMovieList(movieList.filter((movie) => {
            const isShort = movie.duration < 40;
            movie.title.includes(filter.search) && filter.shorts === isShort;
        }))
    }

    const handleSearchText = (evt) => {
        setFilter({
            ...filter,
            search: evt.target.value
        })
    }

    return (
        <main>
            <SearchForm
                showShorts={filter.shorts}
                handleShortsTumbler={handleShortsTumbler}
                filter={filter}
                handleSearchText={handleSearchText}
            />
            <MoviesCardList movieList={movieList}/>
        </main>
    );
};

export default Movies;
